import React, { Component } from 'react';
import reactDOM from 'react-dom';
import {Link} from 'react-router-dom';
import '../index.css'
import $ from 'jquery';

export default class Form extends Component{
    constructor(props){
        super(props);
        this.state={
            fname: '',
            lname: ''
        };
    }

    handleSignup = () => {
        $("#main").animate({left:"22.5%"},450);
        $("#main").animate({left:"30%"},500);
        $("#loginform").css("visibility","hidden");
        $("#loginform").animate({left:"25%"},450);
    
        $("#signupform").animate({left:"17%"},450);
        $("#signupform").animate({left:"30%"},500);
        $("#signupform").css("visibility","visible");
    }

    handleLogin = () => {
        $("#main").animate({left:"77.5%"},450);
    $("#main").animate({left:"70%"},500);
    $("#signupform").css("visibility","hidden");
    $("#signupform").animate({left:"75%"},450);

    $("#loginform").animate({left:"83%"},450);
    $("#loginform").animate({left:"70%"},500);
    $("#loginform").css("visibility","visible");
    }

    submitHandler = (e) =>
    {
        e.preventDefault();
        document.getElementById('login').reset()
      //  alert('you are submitted');
    }

    onChange=(e)=>
    {
        let f = e.target.value;
        let l = e.target.value;
        this.setState({
            fname: f,
            lname: l
        })
    }

    render()
    {
        return(
            // <form onSubmit = {this.submitHandler} id="login">
            //     <input type="text" name="first name" onChange="this.onChange" placeholder="first name"/><br/>
            //     <input type="text" placeholder="first name" onChange="this.onChange"/><br/>
            //     <input type='submit' />
            // </form>
            <div id="form-div">
            <div id="box">
                <div id="main">

                </div>
                <div id="loginform">
                    <h1>Login</h1>
                    <input type="email" placeholder="Email" /><br />
                    <input type="password" placeholder="Password" /><br />
                    <button type="button" name="button">Login</button>
                </div>
                <div id="signupform">
                    <h1>SIGN UP</h1>
                    <input type="text" placeholder="Full Name" /><br />
                    <input type="email" placeholder="Email" /><br />
                    <input type="password" placeholder="Password" /><br />
                    <button type="button">SIGN UP</button>
                </div>

                <div id="login_msg">
                Have an account?
                </div>

                <div id="signup_msg">
                Create an account?
                </div>
                <div>
                    <button type="button" name="button" id="login_btn" onClick={this.handleLogin}>LOGIN</button>
                    <button type="button" name="button" id="signup_btn" onClick={this.handleSignup}>SIGN UP</button>
                </div>
            </div>
            </div>
        )
    }
}
