import React, { Component } from 'react';
import reactDOM from 'react-dom';
import {Link} from 'react-router-dom';

export default class Form extends Component{
    constructor(props){
        super(props);
        this.state={
            fname: '',
            lname: ''
        };
    }

    submitHandler = (e) =>
    {
        e.preventDefault();
        document.getElementById('login').reset()
      //  alert('you are submitted');
    }

    onChange=(e)=>
    {
        let f = e.target.value;
        let l = e.target.value;
        this.setState({
            fname: f,
            lname: l
        })
    }

    render()
    {
        return(
            // <form onSubmit = {this.submitHandler} id="login">
            //     <input type="text" name="first name" onChange="this.onChange" placeholder="first name"/><br/>
            //     <input type="text" placeholder="first name" onChange="this.onChange"/><br/>
            //     <input type='submit' />
            // </form>
            <div id="box">
                <div id="main">

                </div>
                <div id="loginform">
                    <h1>Login</h1>
                    <input type="email" placeholder="Email" /><br />
                    <input type="password" placeholder="Password" /><br />
                    <button type="button" name="button">Login</button>
                </div>
                <div id="signupform">
                    <h1>SIGN UP</h1>
                    <input type="text" placeholder="Full Name" /><br />
                    <input type="email" placeholder="Email" /><br />
                    <input type="password" placeholder="Password" /><br />
                    <button type="button">SIGN UP</button>
                </div>

                <div id="login_msg">
                Have an account?
                </div>

                <div id="signup_msg">
                Create an account?
                </div>
                <div>
                    <button type="button" name="button" id="login_btn">LOGIN</button>
                    <button type="button" name="button" id="signup_btn">SIGN UP</button>
                </div>
            </div>
        )
    }
}
