import React, { Component } from 'react';
import reactDOM from 'react-dom';
import {Link} from 'react-router-dom';

export default class Form extends Component{
    constructor(props){
        super(props);
        this.state={
            fname: '',
            lname: ''
        };
    }

    submitHandler = (e) =>
    {
        e.preventDefault();
        this.setState({
            fname: '',
            lname: ''
        })
        alert('you are submitted');
    }

    myChangeHandler = (e) =>
    {
        let f = e.target.value;
        let l = e.target.value;
        this.setState({
            fname: f,
            lname: l
        })
    }

    render()
    {
        return(
            <form onSubmit = {this.submitHandler}>
                <input type="text" name="first name" onChange="this.myChangeHandler" placeholder="first name"/><br/>
                <input type="text" placeholder="first name" onChange="this.myChangeHandler"/><br/>
                <input type='submit'/>
            </form>
        )
    }
}
