import React, { Component } from 'react';
import reactDOM from 'react-dom';
import {Link} from 'react-router-dom';

export default class Form extends Component{
    constructor(props){
        super(props);
        this.state={
            fname: '',
            lname: ''
        };
    }

    submitHandler = (e) =>
    {
        e.preventDefault();

        alert('you are submitted');
    }

    onChangeHandler = (e) =>
    {
        let f = e.target.value;
        let l = e.target.value;
        this.setState({
            fname: f,
            lname: l
        })
    }

    render()
    {
        return(
            <form onSubmit = {this.submitHandler} name="login">
                <input type="text" name="first name" onChange="this.onChangeHandler" placeholder="first name"/><br/>
                <input type="text" placeholder="first name" onChange="this.onChangeHandler"/><br/>
                <input type='submit'/>
            </form>
        )
    }
}
